import React, { useState } from 'react';

function App() {
  const [text, setText] = useState('');
  const [sentiment, setSentiment] = useState('');
  const [polarity, setPolarity] = useState(null);

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Call Flask API for sentiment analysis
    const response = await fetch('http://15.206.167.230:5000/sentiment', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ text }),
    });

    const data = await response.json();

    // If the API responds with a sentiment, set the state
    if (data.sentiment) {
      setSentiment(data.sentiment);
      setPolarity(data.polarity);  // Save polarity score
    } else {
      setSentiment('Error analyzing sentiment');
    }
  };

  return (
    <div className="App">
      <h1>Sentiment Analysis</h1>
      <form onSubmit={handleSubmit}>
        <textarea
          value={text}
          onChange={handleTextChange}
          placeholder="Enter text for analysis"
        />
        <button type="submit">Analyze</button>
      </form>
      {sentiment && (
        <div>
          <h2>Sentiment: {sentiment}</h2>
          {polarity !== null && <p>Polarity: {polarity}</p>}
        </div>
      )}
    </div>
  );
}

export default App;
